import React, { Component } from 'react'
import { Text, View, ScrollView, Alert, Modal, StyleSheet, Pressable } from "react-native";
import Card from '../Card';
import Header from '../Header';

/* modal was commented out since it's still under construction */

/*const [modalVisible, setModalVisible] = useState(false);*/

export default function Discover() {
    return (
        
        <ScrollView showsVerticalScrollIndicator={false} style={{ flexShrink: 0 }}>
            <View style = {{ paddingHorizontal: 20 }}>
            <Header text="EVENTS | CALENDAR" />
            <TouchableOpacity activeOpacity={0.5}>
            <View style = {{ ...props.style, marginBottom: 20 }}>
                <Image style = {{ width: 'auto', height: props.height }} source = {props.img} />
                <View style = {{ backgroundColor: 'none', padding: 15 }}>
                    <Text style = {{ fontFamily: 'OpenSans_600SemiBold', color: 'black', fontSize: 16, marginBottom: 6 }}>{props.title}</Text>
                    {props.desc ? <Text style = {{ fontFamily: 'OpenSans_400Regular', color: 'gray', fontSize: 14, marginBottom: 10 }}>{props.desc}</Text> : null}
                    <View style = {{ flexDirection: 'row', alignItems: 'center', marginBottom: 6 }}>
                        <Ionicons name="ios-calendar-outline" size={16} />
                        <Text style = {{ fontFamily: 'OpenSans_400Regular', marginLeft: 6 }}>{props.time}</Text>
                    </View>
                </View>
            </View>
        </TouchableOpacity>
            <Text style={{ fontFamily: 'OpenSans_700Bold', fontSize: 24, alignSelf: 'flex-start', marginBottom: 8 }}>Upcoming Events</Text>
            <Card img={require('./../../assets/banner.jpeg')} title="Event Name" desc="Event Description" height={0} time="Mon, Jan 1, 00:00 - 24:00" />
            <Card img={require('./../../assets/banner.jpeg')} title="Event Name" desc="Event Description" height={0} time="Mon, Jan 1, 00:00 - 24:00" />
            </View>
        </ScrollView>
        
    );
}

