import React, { Component } from 'react'
import { Text, View, ScrollView, Alert, Modal, StyleSheet, Pressable } from "react-native";
import DonateCard from '../Card';

export default function Donate() {
    const [giveAsiaVisible, setGiveAsiaVisible] = useState(false);
    return (

        <ScrollView showsVerticalScrollIndicator={false} style={{ flexShrink: 0 }}>
            <View style = {{ paddingHorizontal: 20 }}>
                <Modal animationType="slide" transparent={true} visible={giveAsiaVisible} onRequestClose={() => {
                    setGiveAsiaVisible(!giveAsiaVisible);
                }}>
                    <View style={StyleSheet.centeredView}>
                        <View style={styles.modalView}>
                            <Text style={styles.modalText}>Visit https://give.asia/charity/sunshine-action to donate online via Give.Asia.</Text>
                            <Pressable style={[styles.button, styles.buttonClose]} onPress={() => setGiveAsiaVisible(!giveAsiaVisible)}>
                                <Text style={styles.textStyle}>Hide</Text>
                            </Pressable>
                        </View>
                    </View>
                </Modal>
            <ScrollView horizontal pagingEnabled contentContainerStyle={{ flexDirection: 'row', paddingVertical: 5, paddingHorizontal: 20 }}>
                <Pressable onPress={() => setGiveAsiaVisible(!giveAsiaVisible)}>
                <DonateCard img={require('./../../assets/giveasialogo.png')} title="Give.Asia" height={150} style = {{ width: 150, marginRight: 20 }} />
                </Pressable>
                <DonateCard img={require('./../../assets/banktransfer.png')} title="Bank Transfer" height={150} style = {{ width: 150, marginRight: 20 }} />
            </ScrollView>
            <ScrollView horizontal pagingEnabled contentContainerStyle={{ flexDirection: 'row', paddingVertical: 5, paddingHorizontal: 20 }}>
                <DonateCard img={require('./../../assets/ggf.png')} title="GoGetFunding" height={150} style = {{ width: 150, marginRight: 20 }} />
                <DonateCard img={require('./../../assets/itemdonation.png')} title="Item Donation" height={150} style = {{ width: 150, marginRight: 20 }} />
            </ScrollView>
            </View>
        </ScrollView>
    );
}

const styles = StyleSheet.create({
    centeredView: {
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginTop: 22
    },
    modalView: {
      margin: 20,
      backgroundColor: "white",
      borderRadius: 20,
      padding: 35,
      alignItems: "center",
      shadowColor: "#000",
      shadowOffset: {
        width: 0,
        height: 2
      },
      shadowOpacity: 0.25,
      shadowRadius: 4,
      elevation: 5
    },
    button: {
      borderRadius: 20,
      padding: 10,
      elevation: 2
    },
    buttonOpen: {
      backgroundColor: "#F194FF",
    },
    buttonClose: {
      backgroundColor: "#2196F3",
    },
    textStyle: {
      color: "white",
      fontWeight: "bold",
      textAlign: "center"
    },
    modalText: {
      marginBottom: 15,
      textAlign: "center"
    }
  });
