import React, { Component } from 'react'
import { Text, View, Button, TouchableHighlight, ScrollView, StyleSheet } from "react-native";
import DonateCard from '../Card';
import AmbassadorsTopBarNavigator from '../AmbassadorsTopNavigator';
import ContactCard from '../ContactCard';

export default function Ambassadors() {
    return (
      
        <ScrollView showsVerticalScrollIndicator={false} style={{ flexShrink: 0 }}>
            <View style = {{ paddingHorizontal: 20 }}>
            <View style={{ height: 200, backgroundColor: "#FFF" }} />
            <View style = {{ ...props.style, shadowColor: 'black', shadowOffset: { width: 2, height: 4 }, shadowRadius: 5, shadowOpacity: 0.3, marginBottom: 5, width: "auto" }}>
                
                <View style = {{ backgroundColor: 'white', padding: 15, marginVertical: 5 }}>
                    <Text style = {{ fontFamily: 'OpenSans_600SemiBold', color: 'black', fontSize: 20, marginBottom: 6 }}>Your Location</Text>
                    {props.desc ? <Text style = {{ fontFamily: 'OpenSans_400Regular', color: 'gray', fontSize: 14, marginBottom: 10 }}>{props.desc}</Text> : null}
                    <View style = {{ flexDirection: 'row', alignItems: 'center', marginBottom: 6 }}>
                        
                        <Text style = {{ fontFamily: 'OpenSans_400Regular', marginLeft: 6 }}>City, State/Province, Country</Text>
                    </View>
                </View>
            </View>
            <AmbassadorsTopBarNavigator />
            
            
            </View>
        </ScrollView>
        
    );
}


