import React, { Component } from 'react';
import { Modal, Text, Button, TouchableHighlight, View, StyleSheet} from "react-native";
import DonateCard from '../Card';

class ModalExample extends Component {
    state = {
        modalVisible: false,
    }
    toggleModal(visible) {
        this.setState({ modalVisible: visible });
    }
    render() {
        return (
            <View style = {StyleSheet.container}>
                <Modal animationType = {"slide"} transparent = {false}
                    visible = {this.state.modalVisible}
                    onRequestClose = {() => { console.log("Modal has been closed.") } }>
                        <View style = {styles.modal}>
                            <Text style = {styles.text}>Modal is open!</Text>

                            <TouchableHighlight onPress = {() => {
                                this.toggleModal(!this.state.modalVisible)}}>
                                    <Text style = {styles.text}>Close Modal</Text>
                            </TouchableHighlight>
                        </View>
                    </Modal>
                    <TouchableHighlight onPress = {() => {this.toggleModal(true)}}>
                        <View style = {{ ...props.style, shadowColor: 'black', shadowOffset: { width: 2, height: 4 }, shadowRadius: 5, shadowOpacity: 0.3, marginBottom: 20 }}>
                            <Image style = {{ width: 'auto', height: props.height }} source = {require('./../../assets/giveasialogo.png')} />
                            <View style = {{ backgroundColor: 'white', padding: 15 }}>
                                <Text style = {{ fontFamily: 'OpenSans_600SemiBold', color: 'black', fontSize: 16, marginBottom: 6 }}>Give.Asia</Text>
                                {props.desc ? <Text style = {{ fontFamily: 'OpenSans_400Regular', color: 'gray', fontSize: 14, marginBottom: 10 }}></Text> : null}
                                <View style = {{ flexDirection: 'row', alignItems: 'center', marginBottom: 6 }}>
                        </View>
                </View>
            </View>
                    </TouchableHighlight>
            </View>
        )
    }
}
export default ModalExample



const styles = StyleSheet.create ({
    container: {
       alignItems: 'center',
       backgroundColor: '#ede3f2',
       padding: 100
    },
    modal: {
       flex: 1,
       alignItems: 'center',
       backgroundColor: '#f7021a',
       padding: 50
    },
    text: {
       color: '#3f2949',
       marginTop: 10
    }
 })
