import React, { Component } from 'react'
import { Text, View, ScrollView } from "react-native";
import Card from '../Card';

export default function Donate() {
    return (
        <ScrollView showsVerticalScrollIndicator={false} style={{ flexShrink: 0 }}>
            <View style = {{ paddingHorizontal: 20 }}>
            <ScrollView horizontal pagingEnabled contentContainerStyle={{ flexDirection: 'row', paddingVertical: 5, paddingHorizontal: 20 }}>
                <Card img={require('./../../assets/giveasialogo.png')} title="Give.Asia" height={125} style = {{ width: 150, marginRight: 20 }} />
                <Card img={require('./../../assets/transfer.jpeg')} title="Bank Transfer" height={125} style = {{ width: 150, marginRight: 20 }} />
            </ScrollView>
            <ScrollView horizontal pagingEnabled contentContainerStyle={{ flexDirection: 'row', paddingVertical: 5, paddingHorizontal: 20 }}>
                <Card img={require('./../../assets/gogetfundinglogo.png')} title="GoGetFunding" height={125} style = {{ width: 150, marginRight: 20 }} />
                <Card img={require('./../../assets/itemdonation.png')} title="Item Donation" height={125} style = {{ width: 150, marginRight: 20 }} />
            </ScrollView>
            </View>
        </ScrollView>
    );
}
